import React, { useEffect, useState } from 'react'
import { Table } from 'antd'
import PostBike from './PostBike'

const Inventory = () => {
    const [bikes, setBikes] = useState([])

    // dummy data
    // const dataSource = [
    //     {
    //         key: 1,
    //         "year": 2016,
    //         "make": "Yamaha",
    //         "model": "R6",
    //         "color": "red", // can literally be a color instead of text
    //         "CC": 600, //  should have cc next to value
    //         "miles": 12991,
    //         "price": 9000, // should have dollar signs next to them to prevent confusion
    //         "NADA": 8500, 
    //         "KBB": 8000,
    //     },
    //     {
    //         key: 2,
    //         "year": 2016,
    //         "make": "Yamaha",
    //         "model": "R6",
    //         "color": "red",
    //         "CC": 600, //  should have cc next to value
    //         "miles": 12991,
    //         "price": 9000, // should have dollar signs next to them to prevent confusion
    //         "NADA": 8500, 
    //         "KBB": 8000,
    //     },
    //     {
    //         key: 3,
    //         "year": 2016,
    //         "make": "Yamaha",
    //         "model": "R6",
    //         "color": "red",
    //         "CC": 600, //  should have cc next to value
    //         "miles": 12991,
    //         "price": 9000, // should have dollar signs next to them to prevent confusion
    //         "NADA": 8500, 
    //         "KBB": 8000,
    //     },
    // ] 

    const columns = [
        {
            title: 'Year',
            dataIndex: 'year',
            key: 'year',
        },
        {
            title: 'Make',
            dataIndex: 'make',
            key: 'make',
        },
        {
            title: 'Model',
            dataIndex: 'model',
            key: 'model',
        },
        {
            title: 'Color',
            dataIndex: 'color',
            key: 'color',
        },
        {
            title: 'CC',
            dataIndex: 'CC',
            key: 'CC',
        },
        {
            title: 'Miles',
            dataIndex: 'miles',
            key: 'miles',
        },
        {
            title: 'Price',
            dataIndex: 'price',
            key: 'price',
        },
        {
            title: 'NADA',
            dataIndex: 'NADA',
            key: 'NADA',
        },
        {
            title: 'KBB',
            dataIndex: 'KBB',
            key: 'KBB',
        },
    ]

    useEffect(() => {
        
        fetch('http://localhost:3000/bikes')
            .then(res => res.json())
            .then(data => setBikes(data))
    }, [bikes])
    

    const updateTable = () => {
        fetch('http://localhost:3000/bikes')
            .then(res => res.json())
            .then(data => setBikes(data))
    }

    const addKeyProp = (tableData) => {
        const newTable = []
        let counter = 1
        tableData.forEach((data) => {
            newTable.push({
                ...data,
                key: counter
            })
            counter += 1
        })
        return newTable
    }
    // information such as sold, date listed, date sold, COGS, revenue, notes, customer, should be hidden. If user clicks icon show more, then a modal will appear with the info

    // add the key prop

    return (
        <div>
            <PostBike updateTable={updateTable} />
            <Table dataSource={addKeyProp(bikes)} columns={columns} />
        </div>
    )
}

export default Inventory
