import React, {useState} from 'react'
import { PlusCircleOutlined } from '@ant-design/icons';
import { Modal, Button, Form, Input } from 'antd';

const PostBike = () => {
    const [showForm, setShowForm] = useState(false)
    const [year, setYear] = useState()
    const [make, setMake] = useState('')
    const [model, setModel] = useState('')
    const [color, setColor] = useState('')
    const [price, setPrice]= useState()
    const [COGS, setCOGS] = useState()
    const [KBB, setKBB] = useState()
    const [NADA, setNADA] = useState()
    const [notes, setNotes] = useState('')
    const [miles, setMiles] = useState()
    const [CC, setCC] = useState()
    const [VIN, setVIN] = useState()
    const [dateBought, setDateBought] = useState()

    const toggleForm = () => {
        setShowForm(!showForm)
    }   

    const submitForm = () => {
        console.log('submitted form')
        setShowForm(false)
    }

    const handleCancel = () => {
        console.log('Canceled form')
        setShowForm(false)
    }

    return (
        <>
           <PlusCircleOutlined onClick={toggleForm} style={{fontSize: 100}}/>
           <Modal 
                title="Add motorcycle" 
                visible={showForm} 
                onOk={submitForm} 
                onCancel={handleCancel}
            >
                <Form
                    name="basic"
                    layout="vertical"
                    labelCol={{
                        span: 8,
                    }}
                    wrapperCol={{
                        span: 16,
                    }}
                    initialValues={{
                        remember: true,
                    }}
                    autoComplete="off"
                    >
                    <Form.Item
                        label="Year"
                        name="year"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="Make"
                        name="make"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="Model"
                        name="model"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="CC"
                        name="cc"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="Miles"
                        name="miles"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="COGS"
                        name="cogs"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="Sales Price"
                        name="price"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="KBB Price"
                        name="kbb"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="NADA Price"
                        name="nada"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        wrapperCol={{
                        offset: 8,
                        span: 16,
                        }}
                    >
                        <Button type="primary" htmlType="submit">
                            Submit
                        </Button>
                    </Form.Item>
                </Form>
           </Modal>
        </>
    )
}

export default PostBike
