import React, { useEffect, useState } from 'react'
import { Table, Space } from 'antd'
import PostBike from './PostBike'
import { DeleteOutlined } from '@ant-design/icons' 

const Inventory = () => {
    const [bikes, setBikes] = useState([])
    const { Column } = Table;

    const columns = [
        {
            title: 'Year',
            dataIndex: 'year',
            key: 'year',
        },
        {
            title: 'Make',
            dataIndex: 'make',
            key: 'make',
        },
        {
            title: 'Model',
            dataIndex: 'model',
            key: 'model',
        },
        {
            title: 'Color',
            dataIndex: 'color',
            key: 'color',
        },
        {
            title: 'CC',
            dataIndex: 'CC',
            key: 'CC',
        },
        {
            title: 'Miles',
            dataIndex: 'miles',
            key: 'miles',
        },
        {
            title: 'Price',
            dataIndex: 'price',
            key: 'price',
        },
        {
            title: 'NADA',
            dataIndex: 'NADA',
            key: 'NADA',
        },
        {
            title: 'KBB',
            dataIndex: 'KBB',
            key: 'KBB',
        },
        {
            title: 'Action',
            key: 'action',
            render: (record) => (
                <Space size="middle">
                    <DeleteOutlined onClick={()=>console.log('record: ', record._id)}/>
                </Space>
            )
        }
    ]

    useEffect(() => {
        fetch('http://localhost:3000/bikes')
            .then(res => res.json())
            .then(data => setBikes(data))
    }, [bikes])
    

    const updateTable = () => {
        fetch('http://localhost:3000/bikes')
            .then(res => res.json())
            .then(data => setBikes(data))
    }

    const addKeyProp = (tableData) => {
        const newTable = []
        let counter = 1
        tableData.forEach((data) => {
            newTable.push({
                ...data,
                key: counter
            })
            counter += 1
        })
        return newTable
    }
    // information such as sold, date listed, date sold, COGS, revenue, notes, customer, should be hidden. If user clicks icon show more, then a modal will appear with the info

    return (
        <div>
            <PostBike updateTable={updateTable} />
            <Table dataSource={addKeyProp(bikes)} columns={columns}>
                <Column
                    title="Action"
                    key="action"
                    render={(text, record) => (
                        <Space size="middle">
                        <a>Invite {record.lastName}</a>
                        <a>Delete</a>
                        </Space>
                    )}
                />
            </Table>
        </div>
    )
}

export default Inventory
